// this attaches JWT to axios
import Axios from './Axios';

const setAuthJwt = (token) => {
    if(token) {
        Axios.defaults.headers.common['Authorization'] = token;
    } else {
        delete Axios.defaults.headers.common['Authorization'];
    }
}

export default setAuthJwt;
